package com.my;

import java.lang.reflect.Array;
import java.util.*;
import java.util.function.BinaryOperator;

/**
 * @Author: shanghang
 * @Project:study
 * @description:5409. 检查一个字符串是否包含所有长度为 K 的二进制子串
 * @Date: 2020/5/30 22:37
 **/
public class HasAllCodes {
    char[] A;
    List<String> strings;
    Map<Integer,List<String>> maps = new HashMap<>();
    public boolean hasAllCodes(String s, int k) {
        A = new char[k];
        return Binary(s,k);
    }

    public boolean Binary(String s,int n){
        if(n==0){
            return s.contains(String.valueOf(A));
        }
        A[n-1] = '0';
        if(!Binary(s,n-1)){
            return false;
        }
        A[n-1] = '1';
        if(!Binary(s,n-1)){
            return false;
        }
        return true;
    }

    public static void main(String[] args) {
        HasAllCodes hasAllCodes = new HasAllCodes();
        hasAllCodes.hasAllCodes("",
                15);
    }
}
